package LengthOfLastWord

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var tests = []struct {
	name     string
	input    string
	expected int
}{
	{"Length is 5 No space before & after, one word", "Hello", 5},
	{"Length is 5 No space before & after, one space between words", "Hello World", 5}, // Red
	{"Length is 4 No space before & after, one space between words", "Hello xxxx", 4},  // Red

	{"Length is 5, one word", "Hello ", 5}, // Red
	{"Length is 5, one space between words", " Hello World ", 5},
	{"Length is 5, more space between words", " Hello  World    efef    tes    ", 3},

	{"Example in Problem", "   fly me   to   the moon  ", 4},
	{"Example in Problem 2", "luffy is still joyboy", 6},

	// Edge case
	{"Edge starting case", "l", 1},
	{"Edge ending case 10^4 chars", "    a                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           ", 1},
}

func TestLengthOfLastWord(t *testing.T) {
	for _, test := range tests {
		t.Run(test.name, func(subtest *testing.T) {
			// subtest.Parallel()
			actual := lengthOfLastWord(test.input)
			assert.Equal(subtest, test.expected, actual)
		})
	}
}
